{{ 'partial-shopify-product-reviews.css' | asset_url | stylesheet_tag }}

<style>
  /* Hide buy buttons block on mobile, show only on desktop */
  @media screen and (max-width: 767px) {
    /* Target the product form within buy_buttons blocks */
    .product-blocks .product-form:not(.visually-hidden),
    .product__block .product-form:not(.visually-hidden),
    .product__meta .product-form:not(.visually-hidden) {
      display: none !important;
    }

    /* Also hide quantity error and gift card recipient form on mobile */
    .product__quantity-error,
    .gift-card-recipient {
      display: none !important;
    }
  }
</style>

{%- comment -%} Updated with Frame Measurements block {%- endcomment -%}
{%- liquid
  assign current_variant = product.selected_or_first_available_variant
  assign featured_media = current_variant.featured_media | default: product.featured_media
  assign first_3d_model = product.media | where: 'media_type', 'model' | first
  assign gallery_size = section.settings.gallery_size
  assign image_aspect_ratio = section.settings.image_aspect_ratio
  assign gallery_style = 'thumbnails'
  assign scroller_id = 'product-' | append: section.id
  assign header_block = section.blocks | where: 'type', 'product_header'
  assign header_block_top_mobile = header_block.first.settings.product_header_always_top_mobile

  # If only single image in gallery display as list
  if product.media.size == 1
    assign gallery_style = 'list'
  endif

  # Account for gutter between columns and page outer space
  if gallery_size == 'small'
    assign desktop_image_container_percentage = 46
  elsif gallery_size == 'medium'
    assign desktop_image_container_percentage = 56
  elsif gallery_size == 'large'
    assign desktop_image_container_percentage = 64
  elsif gallery_size == 'xlarge'
    assign desktop_image_container_percentage = 72
  endif

  assign desktop_image_percentage_width = desktop_image_container_percentage

  capture desktop_image_sizes_attribute
    echo '[[dipw]]vw' | replace: '[[dipw]]', desktop_image_percentage_width
  endcapture

  assign product_url = shop.url | append: product.url
  assign featured_media_size = featured_media.width | append: 'x'

  if product.featured_media
    assign product_featured_image_url = product.featured_media | image_url: featured_media_size | prepend: 'https:' | json
  endif
-%}

{%- capture mobile_product_media -%}
  {% for media in product.media -%}
    {% liquid
      assign prioritize_loading = false

      if forloop.first
        assign prioritize_loading = true
      endif
    -%}

    <div
      class="product__media-item"
      data-media-item-id="{{ media.id }}"
      data-media-type="{{ media.media_type }}"
      data-product-media-wrapper
      data-aspect-ratio="{{ image_aspect_ratio }}"
      {% if media.id == featured_media.id -%}
        data-initial-slide
      {%- endif %}
      data-slide-index="{{ forloop.index0 }}"
    >
      {% render 'media',
        fallback_alt: product.title,
        media: media,
        enable_product_lightbox: section.settings.enable_product_lightbox,
        loop_video: section.settings.enable_video_looping,
        aspect_ratio: image_aspect_ratio,
        sizes: '90vw',
        prioritize_loading: prioritize_loading,
        within_carousel: true
      -%}
    </div>
  {%- else -%}
    <div class="product__media-item placeholder-image">
      {{ 'image' | placeholder_svg_tag: 'placeholder-svg' }}
    </div>
  {%- endfor %}
{%- endcapture -%}

{% liquid
  assign section_class = 'product'

  if section.settings.show_recommended_products_on_desktop
    assign section_class = section_class | append: ' product--has-sidebar'
  endif

  if settings.enable_section_animations and section.settings.section_style_enable_animation
    assign section_class = section_class | append: ' animation animation--product'
  endif
-%}

<div
  class="{{ section_class }}"
  data-section-id="{{ section.id }}"
  data-section-type="product"
  data-scroller-id="{{ scroller_id }}"
  data-current-product-id="{{ current_variant.id }}"
  data-product-has-only-default-variant="{{ product.has_only_default_variant }}"
  data-enable-sticky-container="{{ section.settings.enable_sticky_product_details }}"
  data-gallery-size="{{ gallery_size }}"
  data-zoom-gallery-has-thumbnails="{{ section.settings.show_thumbs_in_product_lightbox }}"
  data-is-full-product="true"
  data-product-rating="{{ product.metafields.reviews.rating.value }}"
  data-header-top-mobile="{{ header_block_top_mobile }}"
>
  <div class="product__inner">
    {% comment %} Added in the event the product header is removed so the page has an h1 tag present {% endcomment %}
    {% if header_block == blank %}
      <h1 class="hidden">{{ product.title }}</h1>
    {% endif %}

    {% if section.settings.show_breadcrumbs or section.settings.show_navigation_buttons -%}
      <div class="product__breadcrumbs">
        {% render 'breadcrumbs',
          show_breadcrumbs: section.settings.show_breadcrumbs,
          show_navigation_buttons: section.settings.show_navigation_buttons
        -%}
      </div>
    {%- endif %}

    {% if header_block_top_mobile == 'above' %}
      {%- render 'product-block-header',
        prod: product,
        block_settings: header_block.first.settings,
        current_variant: current_variant,
        featured_media: featured_media,
        is_top: true,
        shopify_attributes: header_block.first.shopify_attributes
      -%}
    {%- endif %}

    <div class="product__top product__primary">
      <div class="product__primary-left">
        <div class="product__media-container below-mobile{% if section.settings.enable_product_lightbox %} lightbox-media-container{% endif %}">
          <div class="product__media">
            {%
              render 'scroll-slider',
              id: scroller_id,
              slider_items: mobile_product_media,
              columns: 1,
              gap: 4,
              peek: 0,
            -%}
          </div>

          {% if first_3d_model -%}
            <div class="product__view-in-space-wrap">
              <button
                aria-label="{{ 'products.product.view_in_space_label' | t }}"
                class="product__view-in-space btn btn--primary btn--full"
                data-in-your-space
                data-shopify-xr
                data-shopify-model3d-id="{{ first_3d_model.id }}"
                data-shopify-title="{{ product.title | escape }}"
                data-shopify-xr-hidden
              >
                <span class="fs-body-75">
                  {% render 'icon', icon: '3d' %}

                  <span class="product__view-in-space-text">{{ 'products.product.view_in_space' | t }}</span>
                </span>
              </button>
            </div>
          {%- endif %}

          {%
            render 'product-thumbnails',
            prod: product,
            featured_media: featured_media,
            aspect_ratio: image_aspect_ratio,
            thumbnail_size: 'mobile',
            thumbnail_position: 'bottom',
          -%}
        </div>

        <div
          class="product__media-container above-mobile{% if section.settings.enable_product_lightbox %} lightbox-media-container{% endif %}"
          data-gallery-style="{{ gallery_style }}"
          data-thumbnails-position="{{ section.settings.thumbnail_position }}"
          data-media-count="{{ product.media.size }}"
        >
          {% if section.settings.thumbnail_position == 'left' -%}
            {%
              render 'product-thumbnails',
              prod: product,
              featured_media: featured_media,
              aspect_ratio: image_aspect_ratio,
              thumbnail_size: section.settings.thumbnail_size,
              thumbnail_position: section.settings.thumbnail_position,
            -%}
          {%- endif %}

          <div class="product__media">
            {% for media in product.media -%}
              {% liquid
                assign media_aspect_ratio = image_aspect_ratio

                if media.media_type == 'model'
                  assign media_aspect_ratio = 'square'
                endif

                assign media_item_class = 'product__media-item animation--item'

                unless media.id == featured_media.id or gallery_style != 'thumbnails'
                  assign media_item_class = media_item_class | append: ' hidden'
                endunless
              -%}

              <div
                class="{{ media_item_class }}"
                data-media-item-id="{{ media.id }}"
                data-media-type="{{ media.media_type }}"
                data-product-media-wrapper
                data-aspect-ratio="{{ media_aspect_ratio }}"
              >
                {% render 'media',
                  media: media,
                  fallback_alt: product.title,
                  enable_product_lightbox: section.settings.enable_product_lightbox,
                  loop_video: section.settings.enable_video_looping,
                  aspect_ratio: media_aspect_ratio,
                  sizes: desktop_image_sizes_attribute,
                  animate: true
                -%}
              </div>
            {%- else -%}
              <div class="product__media-item placeholder-image">
                {{ 'image' | placeholder_svg_tag: 'placeholder-svg' }}
              </div>
            {%- endfor %}
          </div>

          {% if section.settings.thumbnail_position == 'bottom' -%}
            {%
              render 'product-thumbnails',
              prod: product,
              featured_media: featured_media,
              aspect_ratio: image_aspect_ratio,
              thumbnail_size: section.settings.thumbnail_size,
              thumbnail_position: section.settings.thumbnail_position,
            -%}
          {%- endif %}
        </div>

        {% liquid
          capture left_side_blocks
            render 'product-blocks', blocks: section.blocks, prod: product, is_quick: false, column: 'left'
          endcapture
        -%}

        {% if left_side_blocks != blank -%}
          <div class="left-side-blocks for-desktop">
            {{ left_side_blocks }}
          </div>
        {%- endif %}
      </div>

      <div class="product__details product__primary-right">
        <div class="product__meta" data-sticky-container>
          <div class="product__meta-content">
            {%
              render 'product-blocks',
              blocks: section.blocks,
              prod: product,
              is_quick: false,
              column: 'right',
              current_variant: current_variant,
              featured_media: featured_media,
              aspect_ratio: image_aspect_ratio,
            -%}

            {% liquid
              # If buy buttons are absent, render a dummy product form
              assign buy_buttons_enabled = section.blocks | where: 'type', 'buy_buttons'

              if buy_buttons_enabled == blank
                render 'product-form', prod: product, hidden: true
              endif
            -%}

            {%- comment -%}
              Live region for announcing updated price and availability to screen readers
            {%- endcomment -%}
            <p
              class="visually-hidden"
              data-product-status
              aria-live="polite"
              role="status"
            ></p>

            {%- comment -%}
              Live region for announcing that the product form has been submitted and the
              product is in the process being added to the cart
            {%- endcomment -%}
            <p
              class="visually-hidden"
              data-loader-status
              aria-live="assertive"
              role="alert"
              aria-hidden="true"
            >
              {{ 'products.product.loader_label' | t }}
            </p>
          </div>

          {% if section.settings.show_recommended_products_on_desktop -%}
            <div class="product__meta-recommendations-sidebar visible">
              {% unless section.settings.recommended_products_heading == blank -%}
                <h3 class="product__meta-recommendations-sidebar-heading fs-accent-bold">
                  {{ section.settings.recommended_products_heading }}
                </h3>
              {%- endunless %}

              <div class="product__meta-recommendations-sidebar-items" data-details-sidebar-items></div>
            </div>
          {%- endif %}
        </div>
      </div>
    </div>

    <div class="product__bottom">
      {% unless left_side_blocks == blank -%}
        <div class="left-side-blocks for-mobile">
          {{ left_side_blocks }}
        </div>
      {%- endunless %}
    </div>
  </div>

  {% if section.settings.show_recommended_products_on_desktop
    or section.settings.show_recommended_products_on_mobile
  -%}
    {% render 'product-sidebar-recommendations',
      prod: product,
      show_on_desktop: section.settings.show_recommended_products_on_desktop,
      show_on_mobile: section.settings.show_recommended_products_on_mobile,
      max_products: section.settings.max_recommended_products,
      show_quick_view: section.settings.enable_quick_view_recommended_products,
      heading: section.settings.recommended_products_heading
    -%}
  {%- endif %}
</div>

{%- comment -%} Render sticky cart component {%- endcomment -%}
{% if product and product.id -%}
  {% render "sticky-cart", product: product %}
{%- endif %}


{%- comment -%} Render lens selector component {%- endcomment -%}
{% if product and product.id -%}
  {% render "lens-selector", product: product %}
{%- endif %}

<style>
  /* Select Lens Button - Under Quantity Selector */
  .select-lens-after-quantity {
    width: 100%;
    margin-top: 16px;
    margin-bottom: 8px;
  }

  .select-lens-btn-inline {
    width: 100%;
    background: linear-gradient(135deg, #FFD700 0%, #FFA500 100%);
    color: #8B1874;
    border: 2px solid #8B1874;
    font-weight: 700;
    text-shadow: 0 1px 2px rgba(255, 255, 255, 0.5);
    box-shadow: 0 3px 10px rgba(139, 24, 116, 0.3), inset 0 1px 3px rgba(255, 255, 255, 0.5);
    padding: 14px 24px;
    font-size: 16px;
    border-radius: 8px;
    cursor: pointer;
    transition: all 0.2s ease;
  }

  .select-lens-btn-inline:hover {
    background: linear-gradient(135deg, #FFA500 0%, #FFD700 100%);
    box-shadow: 0 4px 15px rgba(255, 215, 0, 0.6), inset 0 1px 3px rgba(255, 255, 255, 0.5);
    transform: translateY(-1px);
  }

  .select-lens-btn-inline span {
    color: #8B1874;
    font-weight: 700;
  }
</style>

<script>
  function openInlineLensSelector(event) {
    event.preventDefault();
    console.log('üîç Inline Select Lens button clicked');

    // Find and click the hidden lens selector trigger button
    const lensSelectorTrigger = document.getElementById('lens-selector-button');
    if (lensSelectorTrigger) {
      console.log('‚úÖ Found lens selector trigger, opening drawer...');
      lensSelectorTrigger.click();
    } else {
      console.warn('‚ö†Ô∏è Lens selector trigger button not found');

      // Fallback: Try to open the lens drawer directly
      const lensDrawer = document.getElementById('lens-drawer');
      if (lensDrawer) {
        lensDrawer.setAttribute('aria-hidden', 'false');
        document.body.style.overflow = 'hidden';
        console.log('‚úÖ Opened lens drawer directly');
      } else {
        console.error('‚ùå Could not find lens drawer');
      }
    }
  }
</script>

{%- assign models = product.media | where: 'media_type', 'model' | json -%}

<script>
  window.ShopifyXR=window.ShopifyXR||function(){(ShopifyXR.q=ShopifyXR.q||[]).push(arguments)}
  ShopifyXR('addModels', {{ models }});
</script>

<script src="//cdn.shopify.com/shopifycloud/shopify-xr-js/assets/v1.0/shopify-xr.en.js" defer="defer"></script>

{% unless product == empty -%}
  <script type="application/json" id="ProductJson-{{ section.id }}">
    {{ product | json }}
  </script>

  <script type="application/json" id="ModelJson-{{ section.id }}">
    {{ product.media | where: 'media_type', 'model' | json }}
  </script>
{%- endunless %}

<script type="application/ld+json">
  {
    "@context": "http://schema.org/",
    "@type": "Product",
    "name": {{ product.title | json }},
    "url": {{ product_url | json }},
    {%- if product.featured_media -%}
      "image": [{{ product_featured_image_url }}],
    {%- endif -%}
    "description": {{ product.description | strip_html | json }},
    {%- if current_variant.sku != blank -%}
      "sku": {{ current_variant.sku | json }},
    {%- endif -%}
    "brand": {
      "@type": "Thing",
      "name": {{ product.vendor | json }}
    },
    "offers": [
      {%- for variant in product.variants -%}
        {
          "@type" : "Offer",
          {%- if variant.sku != blank -%}
            "sku": {{ variant.sku | json }},
          {%- endif -%}
          "availability" : "http://schema.org/{% if variant.available %}InStock{% else %}OutOfStock{% endif %}",
          "price" : {{ variant.price | divided_by: 100.00 | json }},
          "priceCurrency" : {{ cart.currency.iso_code | json }},
          "url" : {{ shop.url | append: variant.url | json }}
        }{% unless forloop.last %},{% endunless %}
      {%- endfor -%}
    ]
  }
</script>

{% schema %}
{
  "name": "t:sections.main_product_overview.name",
  "class": "main-product-section",
  "tag": "section",
  "settings": [
    {
      "type": "checkbox",
      "id": "enable_sticky_product_details",
      "label": "t:sections.main_product_overview.settings.enable_sticky_product_details.label",
      "default": true
    },
    {
      "type": "checkbox",
      "id": "show_breadcrumbs",
      "label": "t:shared.settings.show_breadcrumbs.label",
      "default": false
    },
    {
      "type": "checkbox",
