        },
        {
          "value": "large",
          "label": "t:sections.main_product_overview.settings.gallery_size.option_large"
        },
        {
          "value": "xlarge",
          "label": "t:sections.main_product_overview.settings.gallery_size.option_xlarge"
        }
      ]
