    {
      "type": "select",
      "id": "thumbnail_position",
      "label": "t:sections.main_product_overview.settings.thumbnail_position.label",
      "default": "left",
      "options": [
        {
          "value": "left",
          "label": "t:sections.main_product_overview.settings.thumbnail_position.option_left"
        },
        {
          "value": "bottom",
          "label": "t:sections.main_product_overview.settings.thumbnail_position.option_bottom"
