          "type": "paragraph",
          "content": "t:sections.main_product_overview.blocks.buy_buttons.settings.paragraph__sticky_atc_bar.content"
        },
        {
          "type": "checkbox",
          "id": "enable_sticky_atc_bar_desktop",
          "label": "t:sections.main_product_overview.blocks.buy_buttons.settings.enable_sticky_atc_bar_desktop.label",
          "default": false
        },
        {
