      {% endif %}
    </div>

    <div class="product-item__meta">
      <div class="product-item__text ta-c">
        <h4 class="product-item__product-title ff-body fs-body-75">
          {%- if placeholder -%}
            {{ 'homepage.onboarding.product_title' | t }}
          {%- else -%}
            <a href="{{ prod.url }}">{{ prod.title }}</a>
          {%- endif -%}
        </h4>

        <p class="product-item__price ff-product-price fs-body-75">
          {% if placeholder %}
            {{ 9999 | money }}
          {% elsif prod.compare_at_price > prod.price %}
            <span class="visually-hidden">{{ 'products.product.regular_price' | t }}</span>
            <s class="t-subdued">{{ prod.compare_at_price | money }}</s>

            {% if prod.price_varies %}
              {%- assign sale_price = prod.price | money -%}
              {{ 'products.product.on_sale_from_html' | t: price: sale_price }}
            {% else %}
              <span class="sale" data-price>{{ prod.price | money }}</span>
            {% endif %}
          {% else %}
            {% if prod.price_varies %}
              {%- assign price = prod.price | money -%}
              {{ 'products.product.from_lowest_price_html' | t: lowest_price: price }}
            {% else %}
              <span class="product-item__price fs-body-75">{{ prod.price | money }}</span>
            {% endif %}
          {% endif %}
        </p>

      </div>

    </div>

  </div>
</div>

files.snippets/product-item-price.liquid:
{% liquid
  comment
    Renders a list of product price displays (ie. regular, sale, unit). Used for product
    listings (ie. collection page, search results).

    Optional parameters:
      - variant: { Object } The associated variant object.
      - product: { Object } The associated product object.
      - show_discount: { Boolean } Whether to show any discounts.
      - discount_format: { String } The discount format. Can be:
        - "sale_text"
        - "percent_off"
        - "minus_percent"
        - "save_percent"
        - "amount_off"
        - "minus_amount"
        - "save_amount"
      - use_wrapper: { Boolean } Whether to add a wrapper to the main content. Defaults to 'true'.
      - wrapper_class: { String } A custom class to be applied to the wrapper.
  endcomment

  if prod != blank
    assign compare_at_price = prod.compare_at_price
    assign price = prod.price
    assign price_varies = prod.price_varies
    assign current_variant = prod.selected_or_first_available_variant
  else
    assign compare_at_price = 2999
    assign price = 1999
  endif

  assign use_wrapper = use_wrapper | default: true, allow_false: true
-%}

{%- capture content -%}
  <span class="product-item__price-main">
    {% if placeholder -%}
      {{ 9999 | money }}
    {%- elsif compare_at_price > price -%}
      <span class="visually-hidden">{{ 'products.product.regular_price' | t }}</span>

      <s class="t-subdued">{{ compare_at_price | money }}</s>

      {% if price_varies -%}
        {%- assign sale_price = price | money -%}

        <span class="sale">{{ 'products.product.on_sale_from_html' | t: price: sale_price }}</span>
      {%- else -%}
        <span class="sale" data-price>{{ price | money }}</span>
      {%- endif %}
    {%- else -%}
      {% if price_varies -%}
        {%- assign price = price | money -%}

        <span>
          {{- 'products.product.from_lowest_price_html' | t: lowest_price: price -}}
        </span>
      {%- else -%}
        <span>{{ price | money }}</span>
      {%- endif %}
    {%- endif %}
  </span>
  {% if show_discount -%}
    {% render 'get-display-discount', prod: prod, format: discount_format, use_data_attribute: false, size_class: "fs-body-60" %}
  {%- endif %}

  {% render 'unit-price', item: current_variant %}
{%- endcapture -%}

{% if use_wrapper %}
  <span class="ff-product-price {{ wrapper_class }}">
    {{ content }}
  </span>
{% else %}
  {{ content }}
{% endif %}

files.snippets/product-rating.liquid:
{% comment %}
  pass in :

  Required
  * prod (product but avoiding collision with globally scoped product)

  Optional
  * rating_style: "regular" or "compact"
  * show_rating_count: boolean (default false)
  * show_when_zero_ratings: boolean (default false)
  * text_class: string
  * count_link_url: string
  * count_link_class: string
{% endcomment %}

{%- liquid
  assign reviews = prod.metafields.reviews
  assign rating_count = 0

  if reviews.rating_count != blank
    assign rating_count = reviews.rating_count
  endif

  assign rating = 0
  if reviews.rating.value != blank and reviews.rating.value.scale_max != blank
    # normalize on a 0-5 scale
    assign rating = reviews.rating.value.rating | times: 5.0 | divided_by: reviews.rating.value.scale_max | round: 1
  endif

  unless rating_style
    assign rating_style = 'regular'
  endunless
-%}

{% if show_rating_count %}
  {% capture rating_count_string %}
    {% if count_link_url %}
      (<a href="{{ count_link_url }}" class="{{ count_link_class }}">{{ rating_count }}</a>)
    {% else %}
      ({{ rating_count }})
    {% endif %}
  {% endcapture %}
{%- endif -%}

{%- capture rating_content -%}
  {%- if rating_count > 0 %}
    {%- render 'star-rating', rating: rating, rating_style: rating_style, class_name: 'product-rating__stars' -%}
    <div class="product-rating__count {{ text_class }}">
      <span>{{ rating }}</span>
      <span>{{- rating_count_string -}}</span>
    </div>
  {%- elsif rating_count == 0 and show_when_zero_ratings -%}
    <div class="star-rating product-rating__stars" data-rating-style="compact">
      {%- render 'icon', icon: 'star-filled', style: '--icon-size: 15px' -%}
    </div>
    <span class="{{ text_class }}">{{ 'products.product.no_reviews' | t }}</span>
  {%- endif -%}
{%- endcapture -%}

{%- if rating_content != blank -%}
  <div class="product-rating" data-rating-style="{{ rating_style }}">
    {{ rating_content }}
  </div>
{%- endif -%}

files.snippets/products-slider-block-item.liqu:
{% liquid
  comment
    Renders a products slider block item.

    Required parameters:
      - slider_item_product: { Object } The slider item's product object.
      - text-alignment: { String } The horizontal text alignment of the product details and
        "Shop now" button.
      - thumbnail_aspect_ratio: { String } The product image aspect ratio.
      - show_vendor: { Boolean } Whether to show the vendor.
      - show_price: { Boolean } Whether to show the price.
      - show_button: { Boolean } Whether to show the "Shop now" button.
      - button_style: { String } The button style.
      - placeholder_name: { String } The name of the placeholder image to use.
  endcomment

  assign text_alignment_letter = text_alignment | slice: 0
  assign item_class = 'product-slider__product ta-' | append: text_alignment_letter
-%}

<div class="{{ item_class }}">
  {% comment %}
    TODO: When we add this parent section back, make sure to use 'grid-slider-item-image-sizes' to pass a 'sizes'
    attribute to this snippet.
  {% endcomment %}
  {%
    render 'image'
    image: slider_item_product.featured_image,
    wrapper_class: 'product-slider-product__image',
